<script>

export default {
  name: 'YouTube',

  data() {
    return { src: null }
  },

  props: {
    title: String,
    url: String
  },

  computed: {
    v() {
      const match = this.url.match(/^https?:\/\/(?:www\.)?youtube\.com\/watch\?v=([^&]+)/)
      return match && match[1]
    },

    dataSrc() {
      const [, query] = this.url.match(/\?(.*)/) || this.url.match(/.*youtu\.be\/(.*)/)
      const params = query.split('&').reduce((res, param) => {
        let [key, val] = param.split('=')
        if (param === key) {
          key = 'v'
          val = param
        }
        return Object.assign(res, { [key]: val })
      }, {})
      const { v, t } = params
      const path = t ? `${v}?start=${t}` : `${v}?`
      return `https://www.youtube-nocookie.com/embed/${path}&autoplay=1&autohide=1&modestbranding=1&color=white&rel=0`
    }
  },

  methods: {
    openVideo() {
      console.log(this.dataSrc)
      this.src = this.dataSrc
    }
  },
}
</script>

<template>
  <a
    :href="url"
    :style="`background-image:url(https://img.youtube.com/vi/${v}/hqdefault.jpg)`"
    @click.prevent="openVideo"
  >
    <iframe
      title="YouTube"
      :src="src"
      :data-src="dataSrc"
      frameborder="0"
      allow="autoplay;encrypted-media;picture-in-picture"
      allowfullscreen
    />
  </a>
</template>

<style scoped>
a {
  display: block;
  position: relative;
  margin: 1rem 0 2rem;
  height: 0;
  overflow: hidden;
  max-width: 100%;
  padding-bottom: 56.25%;
  background-repeat: no-repeat;
  background-position: 50%;
  background-size: cover;
  background-color: var(--btcpay-bg-dark);
}

a:before {
  background: url("data:image/png;base64, ") no-repeat center center;
  background-size: auto;
  background-size: 64px 64px;
  position: absolute;
  height: 100%;
  width: 100%;
  opacity: 0.8;
  transition: all 0.2s ease-out;
  content: '';
  cursor: pointer;
}

a iframe {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: none;
}

a iframe[src] {
  display: block;
}
</style>
